&test;




&basemap-relief;


	landuse-lowzoom

		(SELECT way,landuse,"natural" FROM landuse) AS landuse 
		lowzoom
		&postgis-settings;




	landuse

		(SELECT way,way_area,landuse,leisure,"natural",wetland,wood,amenity,crop FROM planet_osm_polygon ORDER BY (CASE WHEN landuse='forest' THEN 0 ELSE 1 END) asc) AS landuse 
		gis
		&postgis-settings;



&hillshade;


	landuse-over-hillshade

		(SELECT way,landuse,"natural",wood,amenity FROM planet_osm_polygon) AS landuse 
		gis
		&postgis-settings;



&basemap-sea;


	water-casing-lowzoom
	water-fill-lowzoom

		(SELECT way,"natural",water,waterway,way_area,name FROM water) AS water 
		lowzoom
		&postgis-settings;




	waterway-lines

		(SELECT way,"CEMT",intermittent,motorboat,tunnel,waterway FROM planet_osm_line) AS water 
		gis
		&postgis-settings;




	waterway-polygons-casing
	waterway-polygons-fill
	water-areas

		(SELECT way,"CEMT",motorboat,"natural",landuse,leisure,waterway,intermittent,way_area FROM planet_osm_polygon) AS water 
		gis
		&postgis-settings;




	landuse-over-water

		(SELECT way,"natural",wetland FROM planet_osm_polygon) AS landuse 
		gis
		&postgis-settings;




	water-weir
	waterway-arrows

		(SELECT way,waterway,intermittent FROM planet_osm_line) AS water 
		gis
		&postgis-settings;




	tunnels-casing
	tunnels-fill
	tunnels-middle

		(SELECT way,ST_Length(way) AS length,highway,service,tracktype,oneway FROM planet_osm_line WHERE (tunnel = 'yes') AND highway IS NOT NULL) AS roads 
		gis
		&postgis-settings;




	cliffs

		(SELECT way,"natural","man_made",highway,barrier FROM planet_osm_line) AS cliff 
		gis
		&postgis-settings;



	cliffs

		(SELECT way,area,"natural","man_made",highway,barrier FROM planet_osm_polygon WHERE (area != 'yes')) AS cliff 
		gis
		&postgis-settings;




	buildings

		(SELECT way,building,leisure,railway,amenity,aeroway FROM planet_osm_polygon WHERE (building IS NOT NULL AND building != 'no') OR (aeroway = 'terminal')) AS buildings 
		gis
		&postgis-settings;




	areas

		(SELECT way,landuse,leisure,way_area FROM planet_osm_polygon) AS area 
		gis
		&postgis-settings;




	contours

		(SELECT wkb_geometry,height FROM contours) AS contours 
		contours
		&postgis-settings;




	borders-lowzoom

		(SELECT way,boundary,admin_level FROM borders) AS borders
		lowzoom
		&postgis-settings;




	borders

		(SELECT way,boundary,admin_level,name FROM planet_osm_line WHERE boundary IN ('administrative')) AS borders 
		gis
		&postgis-settings;




	barriers

		(SELECT way,barrier,historic,fenced,ST_Perimeter(way) AS length FROM planet_osm_polygon) AS barriers 
		gis
		&postgis-settings;



	barriers

		(SELECT way,barrier,historic,fenced,ST_Length(way) AS length FROM planet_osm_line) AS barriers 
		gis
		&postgis-settings;




	railways-lowzoom

		(SELECT way,railway,"service",tunnel FROM railways) AS railways
		lowzoom
		&postgis-settings;




	railways-embankment
	railways

		(SELECT way,railway,tunnel,embankment,service FROM planet_osm_line WHERE railway IS NOT NULL) AS railways 
		gis
		&postgis-settings;




	airports-casing
	airports-fill

		(SELECT way,aeroway FROM planet_osm_line WHERE aeroway IS NOT NULL) AS airports 
		gis
		&postgis-settings;




	roads-casing-lowzoom
	roads-fill-lowzoom

		(SELECT way,highway FROM roads) AS roads 
		lowzoom
		&postgis-settings;




	roads-casing
	roads-fill

		(SELECT way,ST_Length(way) AS length,highway,name,service,surface,tracktype,lanes,embankment,oneway FROM planet_osm_line WHERE (!scale_denominator! > 300000) OR ((bridge IS NULL OR bridge = 'no') AND (tunnel IS NULL OR tunnel != 'yes')) AND highway IS NOT NULL ORDER BY (CASE WHEN highway IN ('motorway','trunk') THEN 5 WHEN highway='primary' THEN 1 WHEN highway='secondary' THEN 2 WHEN highway='tertiary' THEN 3 ELSE 4 END) DESC ) AS roads
		gis
		&postgis-settings;




	railway-points

		(SELECT way,railway,subway FROM planet_osm_point) AS symbols 
		gis
		&postgis-settings;





	ferry-routes

		(SELECT way,route FROM planet_osm_line) AS route 
		gis
		&postgis-settings;




	railway-bridges-casing
	railway-bridges

		(SELECT way,ST_Length(way) AS length,railway,layer FROM planet_osm_line WHERE (bridge != 'no') AND (railway IS NOT NULL) AND (layer IS NULL OR layer <= '1')) AS railways 
		gis
		&postgis-settings;



	railway-bridges-casing
	railway-bridges

		(SELECT way,ST_Length(way) AS length,railway,layer FROM planet_osm_line WHERE (bridge != 'no') AND (railway IS NOT NULL) AND (layer > '1')) AS railways 
		gis
		&postgis-settings;




	bridges-casing
	bridges-fill
	bridges-middle
	road-names-text

		(SELECT way,ST_Length(way) AS length,highway,service,surface,tracktype,layer,name,railway,oneway FROM planet_osm_line WHERE (bridge != 'no') AND (highway IS NOT NULL OR railway = 'tram') AND (layer IS NULL OR layer <= '1')) AS roads 
		gis
		&postgis-settings;




	bridges-casing
	bridges-fill
	bridges-middle
	road-names-text

		(SELECT way,ST_Length(way) AS length,highway,service,surface,tracktype,layer,name,railway,oneway FROM planet_osm_line WHERE (bridge != 'no') AND (highway IS NOT NULL OR railway = 'tram') AND (layer = '2')) AS roads 
		gis
		&postgis-settings;




	bridges-casing
	bridges-fill
	bridges-middle
	road-names-text

		(SELECT way,ST_Length(way) AS length,highway,service,surface,tracktype,layer,name,railway,oneway FROM planet_osm_line WHERE (bridge != 'no') AND (highway IS NOT NULL OR railway = 'tram') AND (layer > '2')) AS roads 
		gis
		&postgis-settings;




	aerialways

		(SELECT way,aerialway FROM planet_osm_line WHERE aerialway IS NOT NULL) AS aerialway 
		gis
		&postgis-settings;




	powerlines

		(SELECT way,power,voltage FROM planet_osm_line) AS powerlines 
		gis
		&postgis-settings;



	powertowers

		(SELECT way,power FROM planet_osm_point WHERE power IN ('tower','pole')) AS powertowers 
		gis
		&postgis-settings;




	text-natural-line

		(SELECT way,name,"natural" FROM planet_osm_line) AS names
		gis
		&postgis-settings;




	text-natural-poly-lowzoom

		(SELECT way,"natural",water,waterway,way_area,name FROM water) AS contours 
		lowzoom
		&postgis-settings;




	symbols-important-point

		(SELECT way,name,railway,train FROM planet_osm_point) AS names
		gis
		&postgis-settings;




	text-natural-poly

		(SELECT way,name,"natural",leisure,way_area FROM planet_osm_polygon) AS names
		gis
		&postgis-settings;




	text-natural-point

		(SELECT way,name,"natural",ele FROM planet_osm_point) AS names
		gis
		&postgis-settings;




	symbols-1
	symbols-2
	symbols-poly

		(SELECT way,access,amenity,highway,historic,"memorial:type",castle_type,religion,denomination,denotation,"man_made","tower:type","communication:radio","communication:television",building,"natural",name,ruins,NULL AS "summit:cross",tourism,"power","generator:source","site_type",sport,type,disused,abandoned,railway,way_area FROM planet_osm_polygon) AS symbols 
		gis
		&postgis-settings;




	symbols-1
	symbols-2
	symbols-point

		(SELECT way,access,amenity,highway,historic,"memorial:type",castle_type,religion,denomination,denotation,"man_made","tower:type","communication:radio","communication:television",building,"natural",name,ruins,"summit:cross",tourism,"power","generator:source","site_type",sport,type,disused,abandoned,railway,train,subway FROM planet_osm_point) AS symbols 
		gis
		&postgis-settings;




	road-names-text

		(SELECT way,ST_Length(way) AS length,highway,name,service,surface,tracktype,lanes,embankment,oneway FROM planet_osm_line WHERE (!scale_denominator! > 300000) OR ((bridge IS NULL OR bridge = 'no') AND (tunnel IS NULL OR tunnel != 'yes')) AND highway IS NOT NULL ORDER BY (CASE WHEN highway IN ('motorway','trunk') THEN 5 WHEN highway='primary' THEN 1 WHEN highway='secondary' THEN 2 WHEN highway='tertiary' THEN 3 ELSE 4 END) DESC ) AS roads
		gis
		&postgis-settings;




	housenumbers-poly

		(SELECT way,"addr:housenumber",name,building,way_area FROM planet_osm_polygon WHERE building IS NOT NULL) AS housenumbers 
		gis
		&postgis-settings;



	housenumbers-point

		(SELECT way,"addr:housenumber",name,building FROM planet_osm_point WHERE "addr:housenumber" IS NOT NULL) AS housenumbers 
		gis
		&postgis-settings;




	text-roads-ref

		(SELECT way,highway,ref FROM planet_osm_line WHERE (ST_Length(way)::integer > 1000) AND ref IS NOT NULL) AS roads 
		gis
		&postgis-settings;




	text-waterways

		(SELECT way,name,waterway FROM planet_osm_line) AS names
		gis
		&postgis-settings;




	text-cities-lowzoom

		(SELECT way,admin_level,name,capital,place,population FROM cities ORDER BY population DESC) AS cities
		lowzoom
		&postgis-settings;




	text-cities
	text-towns
	text-villages

		(SELECT way,admin_level,name,capital,'('||substring(ele from '^[\d]+')||')' as ele,place,population::INTEGER FROM planet_osm_point WHERE (population IS NULL OR population SIMILAR TO '[[:digit:]]+') AND place IN ('city','town','village','suburb','hamlet','isolated_dwelling','locality') ORDER BY (CASE WHEN place='city' THEN 0 WHEN place='town' THEN 1 WHEN place='village' THEN 2 ELSE 3 END) ASC, (CASE WHEN population IS NUll THEN 0 WHEN population similar to '[[:digit:]]+' THEN population::integer ELSE 0 END) DESC
	  ) AS names
		gis
		&postgis-settings;
