		landuse-lowzoom

			(SELECT way,landuse,"natural" FROM landuse) AS landuse 
			lowzoom
			&postgis-settings;






		landuse

			(SELECT way,way_area,landuse,leisure,"natural",wetland,leaf_type,amenity,crop,orchard FROM planet_osm_polygon WHERE (landuse IS NOT NULL) OR (leisure IS NOT NULL) OR ("natural" IS NOT NULL) OR (wetland IS NOT NULL) OR (leaf_type IS NOT NULL) OR (amenity IS NOT NULL) OR (crop IS NOT NULL) OR (orchard IS NOT NULL) ORDER BY (CASE WHEN landuse='forest' THEN 0 ELSE 1 END) asc) AS landuse 
			gis
			&postgis-settings;



        &basemap-relief;


		landuse-over-hillshade

			(SELECT way,landuse,"natural",leaf_type,amenity,building,power FROM planet_osm_polygon WHERE (landuse IS NOT NULL) OR ("natural" IS NOT NULL) OR (leaf_type IS NOT NULL) OR (amenity IS NOT NULL) OR (building IS NOT NULL) OR (power IS NOT NULL)) AS landuse 
			gis
			&postgis-settings;



	&basemap-sea;


		road-areas

			(SELECT way,highway FROM planet_osm_polygon WHERE highway IS NOT NULL) AS roadareas
			gis
			&postgis-settings;




		water-casing-lowzoom
		water-fill-lowzoom

			(SELECT way,"natural",water,waterway,way_area,name FROM water) AS water 
			lowzoom
			&postgis-settings;




		waterway-lines

			(SELECT way,"CEMT",intermittent,motorboat,tunnel,waterway FROM planet_osm_line WHERE ("CEMT" IS NOT NULL) OR (intermittent IS NOT NULL) OR (motorboat IS NOT NULL) OR (tunnel IS NOT NULL) OR (waterway IS NOT NULL)) AS water 
			gis
			&postgis-settings;




		waterway-polygons-casing
		waterway-polygons-fill
		water-areas

			(SELECT way,"CEMT",motorboat,"natural",landuse,leisure,waterway,intermittent,way_area FROM planet_osm_polygon WHERE (("CEMT" IS NOT NULL) OR (motorboat IS NOT NULL) OR ("natural" IS NOT NULL) OR (landuse IS NOT NULL) OR (leisure IS NOT NULL) OR (waterway IS NOT NULL) OR (intermittent IS NOT NULL))) AS water 
			gis
			&postgis-settings;




		landuse-over-water

			(SELECT way,"natural",wetland FROM planet_osm_polygon WHERE ("natural" IS NOT NULL) OR (wetland IS NOT NULL)) AS landuse 
			gis
			&postgis-settings;




		water-weir
		waterway-arrows

			(SELECT way,waterway,intermittent FROM planet_osm_line WHERE (waterway IS NOT NULL) OR (intermittent IS NOT NULL)) AS water 
			gis
			&postgis-settings;




		tunnels-casing
		tunnels-fill
		tunnels-middle

			(SELECT way,ST_Length(way) AS length,highway,service,tracktype,oneway,attraction,sport,leisure,surface FROM planet_osm_line WHERE (tunnel = 'yes') AND (highway IS NOT NULL OR attraction='summer_toboggan' OR leisure='track')) AS roads  
			gis
			&postgis-settings;




		cliffs

			(SELECT way,"natural","man_made",highway,barrier,'no' as area FROM planet_osm_line WHERE ("natural" IS NOT NULL) OR ("man_made" IS NOT NULL) OR (highway IS NOT NULL) OR (barrier IS NOT NULL)) AS cliff 
			gis
			&postgis-settings;



		cliffs

			(SELECT way,"natural","man_made",highway,barrier,'yes' as area FROM planet_osm_polygon WHERE ("natural" IS NOT NULL) OR ("man_made" IS NOT NULL) OR (highway IS NOT NULL) OR (barrier IS NOT NULL)) AS cliff 
			gis
			&postgis-settings;




		buildings

			(SELECT way,building,leisure,railway,amenity,aeroway FROM planet_osm_polygon WHERE (building IS NOT NULL AND building != 'no') OR (aeroway = 'terminal')) AS buildings 
			gis
			&postgis-settings;




		areas

			(SELECT way,landuse,leisure,way_area FROM planet_osm_polygon WHERE (landuse IS NOT NULL) OR (leisure IS NOT NULL)) AS area 
			gis
			&postgis-settings;


&contours;


		borders-lowzoom

			(SELECT way,boundary,admin_level FROM borders) AS borders
			lowzoom
			&postgis-settings;




		borders

			(SELECT way,boundary,admin_level,name FROM planet_osm_line WHERE boundary IN ('administrative')) AS borders 
			gis
			&postgis-settings;




		barriers

			(SELECT way,barrier,historic,fenced,"natural",ST_Perimeter(way) AS length FROM planet_osm_polygon WHERE (barrier IS NOT NULL) OR (historic IS NOT NULL) OR (fenced IS NOT NULL) OR ("natural" IS NOT NULL)) AS barriers 
			gis
			&postgis-settings;



		barriers

			(SELECT way,barrier,historic,fenced,"natural",ST_Length(way) AS length FROM planet_osm_line WHERE (barrier IS NOT NULL) OR (historic IS NOT NULL) OR (fenced IS NOT NULL) OR ("natural" IS NOT NULL)) AS barriers 
			gis
			&postgis-settings;




		railways-lowzoom

			(SELECT way,railway,"service",tunnel FROM railways) AS railways
			lowzoom
			&postgis-settings;




		railways-embankment
		railways

			(SELECT way,railway,tunnel,embankment,service FROM planet_osm_line WHERE railway IS NOT NULL) AS railways 
			gis
			&postgis-settings;




		airports-casing
		airports-fill

			(SELECT way,aeroway FROM planet_osm_line WHERE aeroway IS NOT NULL) AS airports_line
			gis
			&postgis-settings;




		airports-poly

			(SELECT way,aeroway,way_area FROM planet_osm_polygon WHERE aeroway IS NOT NULL) AS airports_poly
			gis
			&postgis-settings;




		roads-casing-lowzoom
		roads-fill-lowzoom

			(SELECT way,highway FROM roads) AS roads 
			lowzoom
			&postgis-settings;




		roads-casing
		roads-fill
		trams

			(SELECT way,ST_Length(way) AS length,highway,construction,name,sac_scale,trail_visibility,via_ferrata_scale,service,surface,tracktype,lanes,embankment,oneway,railway,leisure,sport,attraction FROM planet_osm_line WHERE (!scale_denominator! > 300000) OR ((bridge IS NULL OR bridge = 'no') AND (tunnel IS NULL OR tunnel != 'yes')) AND (highway IS NOT NULL OR railway='tram' OR leisure='track' OR attraction='summer_toboggan') ORDER BY (CASE WHEN highway IN ('motorway','trunk') THEN 5 WHEN highway='primary' THEN 1 WHEN highway='secondary' THEN 2 WHEN highway='tertiary' THEN 3 ELSE 4 END) DESC ) AS roads 
			gis
			&postgis-settings;




		railway-points

			(SELECT way,railway,station,subway FROM planet_osm_point WHERE (railway IS NOT NULL) OR (station IS NOT NULL) OR (subway IS NOT NULL)) AS symbols 
			gis
			&postgis-settings;




		ferry-routes

			(SELECT way,route FROM planet_osm_line WHERE route IS NOT NULL) AS route 
			gis
			&postgis-settings;




		railway-bridges-casing
		railway-bridges

			(SELECT way,ST_Length(way) AS length,railway,service,layer FROM planet_osm_line WHERE (bridge != 'no') AND (railway IS NOT NULL) AND (layer IS NULL OR layer <= '1')) AS railways 
			gis
			&postgis-settings;




		railway-bridges-casing
		railway-bridges

			(SELECT way,ST_Length(way) AS length,railway,service,layer FROM planet_osm_line WHERE (bridge != 'no') AND (railway IS NOT NULL) AND (layer > '1')) AS railways 
			gis
			&postgis-settings;




