		road-names-text

			(SELECT way,ST_Length(way) AS length,highway,construction,service,surface,tracktype,layer,name,railway,oneway,leisure,sport,attraction FROM planet_osm_line WHERE (bridge != 'no') AND (highway IS NOT NULL OR railway = 'tram' OR leisure='track' OR attraction='summer_toboggan') AND (layer IS NULL OR layer <= '1')) AS roads  
			gis
			&postgis-settings;




		bridges-casing
		bridges-fill
		bridges-middle
		trams
		road-names-text

			(SELECT way,ST_Length(way) AS length,highway,construction,service,surface,tracktype,layer,name,railway,oneway,leisure,sport,attraction FROM planet_osm_line WHERE (bridge != 'no') AND (highway IS NOT NULL OR railway = 'tram' OR leisure='track' OR attraction='summer_toboggan') AND (layer = '2')) AS roads  
			gis
			&postgis-settings;




		bridges-casing
		bridges-fill
		bridges-middle
		trams
		road-names-text

			(SELECT way,ST_Length(way) AS length,highway,construction,service,surface,tracktype,layer,name,railway,oneway,leisure,sport,attraction FROM planet_osm_line WHERE (bridge != 'no') AND (highway IS NOT NULL OR railway = 'tram' OR leisure='track' OR attraction='summer_toboggan') AND (layer > '2')) AS roads  
			gis
			&postgis-settings;




		aerialways

			(SELECT way,aerialway FROM planet_osm_line WHERE aerialway IS NOT NULL) AS aerialway 
