		text-water-lowzoom

            (SELECT way,' '||name||' ' AS name,lake_area FROM lakelabels WHERE label='lakeaxis' OR label='bayaxis' OR label='straitaxis') AS lakelabels 
			lowzoom
			&postgis-settings;




		text-natural-poly

			(SELECT way,name,"natural",leisure,way_area FROM planet_osm_polygon WHERE (name IS NOT NULL) OR ("natural" IS NOT NULL) OR (leisure IS NOT NULL)) AS names
			gis
			&postgis-settings;




		symbols-sport

			(SELECT way,(pitchdata).icon AS icon,(pitchdata).angle AS angle,(pitchdata).pitch_area AS pitch_area,(pitchdata).labelsizefactor AS labelsizefactor FROM (select way,getpitchicon(way,sport) as pitchdata FROM planet_osm_polygon WHERE leisure='pitch' AND (building IS NULL OR building = 'no')) AS pitches) AS symbols 
			gis
			&postgis-settings;




		symbols-stations

			(SELECT way,name,railway,train,subway,station,direction FROM planet_osm_point WHERE (railway='station' OR railway='halt') AND ((direction IS NULL) OR direction SIMILAR TO '[0-9]+')) AS names
			gis
			&postgis-settings;




		symbols-peaks
        text-peaks

			(SELECT way,"natural",name,ele,"summit:cross",otm_isolation::INTEGER FROM planet_osm_point WHERE ("natural" = 'peak' or "natural" = 'volcano') ORDER BY otm_isolation::INTEGER DESC) AS symbols 
			gis
			&postgis-settings;




		symbols-1
		symbols-2
		symbols-poly
		symbols-road


			(SELECT way,access,amenity,highway,historic,"memorial:type",castle_type,religion,denomination,denotation,"man_made","tower:type","communication:radio","communication:television",'('||substring(height from '^[\d]+')||')' as height,building,"natural",name,ruins,NULL AS "summit:cross",tourism,"power","generator:source","site_type",leisure,sport,leaf_type,disused,abandoned,railway,otm_isolation::INTEGER,hiking,way_area FROM planet_osm_polygon) AS symbols 
			gis
			&postgis-settings;




		symbols-1
		symbols-2
		symbols-point
		symbols-road

			(SELECT way,access,amenity,highway,historic,"memorial:type",castle_type,leaf_type,religion,denomination,denotation,"man_made","tower:type","communication:radio","communication:television",'('||substring(height from '^[\d]+')||')' as height,building,"natural",name,ruins,"summit:cross",tourism,"power","generator:source","site_type",sport,disused,abandoned,railway,train,subway,otm_isolation::INTEGER,hiking FROM planet_osm_point) AS symbols 
			gis
			&postgis-settings;




		symbols-saddle

			(SELECT way,"natural",direction,name,ele FROM planet_osm_point where ("natural"='saddle' or "natural"='col' or "natural"='notch') AND direction SIMILAR TO '[0-9]+') AS symbols 
			gis
			&postgis-settings;




		text-glacier-lowzoom

            (SELECT way,name,lake_area FROM lakelabels WHERE label='glacieraxis') AS lakelabels 
			lowzoom
			&postgis-settings;




	 text-natural-areas-highzoom

	   (SELECT way,name,areatype,way_area,nextregionsize,subregionsize FROM naturalarealabels) AS naturallabels 
	   lowzoom
	   &postgis-settings;




		symbols-viewpoint

			(SELECT way,tourism,(int_dir).s1 as firstrotation,(int_dir).a1 as firstangle,(int_dir).s2 as secondrotation,(int_dir).a2 as secondangle FROM (SELECT way,tourism,viewpointdirection(direction) AS int_dir FROM planet_osm_point WHERE (tourism='viewpoint' AND direction IS NOT NULL)) AS viewpoints) AS symbols
			gis
			&postgis-settings;




		road-names-text

			(SELECT way,ST_Length(way) AS length,highway,name,service,surface,tracktype,lanes,embankment,oneway FROM planet_osm_line WHERE (!scale_denominator! > 300000) OR ((bridge IS NULL OR bridge = 'no') AND (tunnel IS NULL OR tunnel != 'yes')) AND ((highway IS NOT NULL AND highway!='construction') OR (leisure='track' AND sport='bobsleigh') OR attraction='summer_toboggan') ORDER BY (CASE WHEN highway IN ('motorway','trunk') THEN 5 WHEN highway='primary' THEN 1 WHEN highway='secondary' THEN 2 WHEN highway='tertiary' THEN 3 ELSE 4 END) DESC ) AS roads 
			gis
			&postgis-settings;




		housenumbers-poly

			(SELECT way,"addr:housenumber",name,building,way_area FROM planet_osm_polygon WHERE building IS NOT NULL) AS housenumbers 
			gis
			&postgis-settings;



		housenumbers-point

			(SELECT way,"addr:housenumber",name,building FROM planet_osm_point WHERE "addr:housenumber" IS NOT NULL) AS housenumbers 
			gis
			&postgis-settings;




		text-roads-ref

			(SELECT way,highway,ref FROM planet_osm_line WHERE (ST_Length(way)::integer > 1000) AND ref IS NOT NULL) AS roads 
			gis
			&postgis-settings;




		text-waterways

			(SELECT way,name,waterway FROM planet_osm_line WHERE waterway IS NOT NULL) AS names
			gis
			&postgis-settings;




		text-cities-lowzoom

			(SELECT way,admin_level,name,capital,place,population FROM cities ORDER BY population DESC) AS cities
			lowzoom
			&postgis-settings;




		text-cities
		text-towns
		text-villages

			(SELECT way,admin_level,name,capital,'('||substring(ele from '^[\d]+')||')' as ele,place,population::INTEGER FROM planet_osm_point WHERE (population IS NULL OR population SIMILAR TO '[[:digit:]]+') AND place IN ('city','town','village','suburb','hamlet','isolated_dwelling','locality') ORDER BY (CASE WHEN place='city' THEN 0 WHEN place='town' THEN 1 WHEN place='village' THEN 2 ELSE 3 END) ASC, (CASE WHEN population IS NUll THEN 0 WHEN population similar to '[[:digit:]]+' THEN population::integer ELSE 0 END) DESC
		  ) AS names
			gis
			&postgis-settings;
